import { createStore } from 'vuex'
import axios from 'axios'

export default createStore({
  state: {
    user: {
      token: localStorage.getItem('token') || '',
      companies: []
    },
    selectedCompany: {
      id: localStorage.getItem('selectedCompanyId') || null,
    }
  },
  mutations: {
    setToken(state, token) {
      state.user.token = token;
      localStorage.setItem('token', token);
    },
    setUserCompanies(state, companies) {
      state.user.companies = companies;
    },
    setSelectedCompany(state, companyId) {
      state.selectedCompany.id = companyId;
      localStorage.setItem('selectedCompanyId', companyId);
    },
    clearAuthData(state) {
      state.user.token = '';
      state.user.companies = [];
      state.selectedCompany.id = null;
      localStorage.removeItem('token');
      localStorage.removeItem('selectedCompanyId');
    }
  },
  actions: {
    login({ commit }, authData) {
      return new Promise((resolve, reject) => {
        axios.post('/api/auth/login/', authData)
          .then(response => {
            const token = response.data.access;
            commit('setToken', token);
            axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            // You would typically fetch user companies here
            resolve(response);
          })
          .catch(error => {
            localStorage.removeItem('token');
            reject(error);
          });
      });
    },
    logout({ commit }) {
      commit('clearAuthData');
      delete axios.defaults.headers.common['Authorization'];
    },
    loadCompanyData({ state }) {
        // This is a placeholder for where you would load company specific data
        console.log(`Loading data for company ${state.selectedCompany.id}`);
    }
  },
  getters: {
    isLoggedIn: state => !!state.user.token,
    selectedCompanyId: state => state.selectedCompany.id
  }
})
